function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var paidBy = g_form.getValue('paid_by');
   var getActiveStatus = new GlideAjax('AuditUtils');
   getActiveStatus.addParam('sysparm_name','getActiveStatus');
   getActiveStatus.addParam('sysId',paidBy);
   getActiveStatus.getXML(owerInfo);

   function owerInfo(response){
	var status = response.responseXML.documentElement.getAttribute('answer');
	alert(status);
   }

}
